<?phpinclude_once '../configs/config.class.php';include_once 'class/SmartyMain.class.php';include_once '../web_addr.php' ;include_once '../session_check.php' ;include_once '../openadodb.php' ;$v = $_REQUEST ;$memo = '' ;if ($v['c'] == '1') {			//插入店家基本資料	$sql = 'SELECT * FROM tBranch WHERE bId="'.$v['s'].'";' ;	$rs = $conn->Execute($sql) ;	$memo = $rs->fields['bCashierOrderMemo'] ;		if ($memo != '') $memo .= "\n\n" ;	$memo .= date("Y-m-d H:i:s")."\n".$v['t'] ;		$sql = 'UPDATE tBranch SET bCashierOrderMemo="'.$memo.'" WHERE bId="'.$v['s'].'";' ;	$conn->Execute($sql) ;	echo 'T' ;}else if ($v['c'] == '2') {		//插入代書基本資料	$sql = 'SELECT * FROM tScrivener WHERE sId="'.$v['s'].'";' ;	$rs = $conn->Execute($sql) ;	$memo = $rs->fields['sRemark4'] ;		if ($memo != '') $memo .= "\n\n" ;	$memo .= date("Y-m-d H:i:s")."\n".$v['t'] ;		$sql = 'UPDATE tScrivener SET sRemark4="'.$memo.'" WHERE sId="'.$v['s'].'";' ;	$conn->Execute($sql) ;	echo 'T' ;}else {	echo 'F' ;}?>