<?phpclass MyClass {	//取得年度option	public function FromToYear($fromYear,$startYear) {		$max = (int)date("Y") ;		$val = '' ;		for ($i = $startYear ; $i <= $max ; $i ++) {			$val .= '<option value="'.$i.'"' ;			if ($fromYear == $i) {				$val .= ' selected="selected"' ;			}			$val .= '>'.$i."</option>\n" ;		}		return $val ;	}	##		//取得月份option	public function FromToMonth($fromMonth) {		$val = '' ;		for ($i = 0 ; $i < 12 ; $i ++) {			$val .= '<option value="'.($i+1).'"' ;			if ($fromMonth == ($i+1)) {				$val .= ' selected="selected"' ;			}			$val .= '>'.str_pad(($i+1),2,'0',STR_PAD_LEFT)."</option>\n" ;		}		return $val ;	}	##}?>