function checkSign($type,$storeId){
  global $conn;

  $sql = "SELECT * FROM tSalesSign WHERE sType = '".$type."' AND sStore = '".$storeId."' AND sSales != 3 AND sSales != 0";
  $rs = $conn->Execute($sql);
  // echo $sql."\r\n";
    $total=$rs->RecordCount();

  if ($total > 0) { //別人簽了
    return false;
  }else{
    return true;
  }
}
